import React, { Component } from "react";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import Footer from "./Footer";
import Nav from "./Nav";
class Register extends Component {
  constructor() {
    super();
    this.state = {
      first_name: null,
      last_name: null,
      email: null,
      password: null,
      confirm_pass: null,
      first_name_mgs: "",
      last_name_mgs: "",
      email_mgs: "",
      password_mgs: "",
      confirm_pass_mgs: "",
      first_name_error: "",
      last_name_error: "",
      email_error: "",
      password_error: "",
      confirm_pass_error: "",
    };
  }
  registerButtonPressed()
  {

  }
  onChangeHandler=(event)=>{
    var inputName=event.target.name;
    var inputValue=event.target.value;
    this.setState({
      [inputName]:inputValue
    })
    if(inputName==="email"){
      if(!this.emailValidation(inputValue)){
      this.setState(
        {
          emailMgs:"* Invalid Email",
          emailError:false,
        }
        )
      }
      else{
        this.setState({
          emailMgs:"",
          emailError:true,
        })
      }
    }

    if(inputName==="password"){
      if(inputValue.length<7){
      this.setState({
        passMgs:"* minimum 6 letters",
        passwordError:false
      })
      }
      else{
        this.setState({
          passMgs:"",
          passwordError:true,
        })
      }
    }

  }
  emailValidation(email)
  {
    const re = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;
    return re.test(String(email).toLowerCase());
  }
  nameValidation(name) {
    var regex = /^[a-zA-Z ]{2,30}$/;
    return regex.test(String(name));
}
  register() {

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/x-www-form-urlencoded");

    var urlencoded = new URLSearchParams();
    urlencoded.append("first_name", this.state.first_name);
    urlencoded.append("last_name", this.state.last_name);
    urlencoded.append("email", this.state.email);
    urlencoded.append("password", this.state.password);

    var requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: urlencoded,
      redirect: "follow",
    };

    fetch("http://127.0.0.1:8000/api/auth/register/", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  }
  render() {
    return (
      <div>
        <Nav/>
        <div className="container all-margin-top text-left minimum-height">
          <h1 className="mt-4 mb-3">
            Register 
            <small> Form</small>
          </h1>
          <ol className="breadcrumb">
            <li className="breadcrumb-item active">Register</li>
          </ol>

          <div className="row">
            <div className="col-lg-4 mb-4">
              <form name="sentMessage" id="contactForm" noValidate>
                <div className="control-group form-group">
                  <div className="controls">
                    <label>First Name*</label>
                    <input
                      type="text"
                      className="form-control"
                      id="first_name"
                      name="first_name"
                      required
                      data-validation-required-message="Please enter your first name."
                      onChange={(event) => {
                        this.setState({ first_name: event.target.value });
                      }}
                    ></input>
                    <p className="help-block"></p>
                  </div>
                </div>
                <div className="controls">
                  <label>Last Name*</label>
                  <input
                    type="text"
                    className="form-control"
                    id="last_name"
                    name="last_name"
                    required
                    data-validation-required-message="Please enter your last name."
                    onChange={(event) => {
                      this.setState({ last_name: event.target.value });
                    }}
                  ></input>
                  <p className="help-block"></p>
                </div>
                <div className="control-group form-group">
                  <div className="controls">
                    <label>Email*</label>
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      name="email"
                      required
                      data-validation-required-message="Please enter your email."
                      onChange={(event) => {
                        this.setState({ email: event.target.value });
                      }}
                    ></input>
                    <p className="help-block"></p>
                  </div>
                </div>
                <div className="control-group form-group">
                  <div className="controls">
                    <label>Password*</label>
                    <input
                      type="password"
                      className="form-control"

                      id="password"
                      name="password"

                      onChange={this.onChangeHandler}
                    ></input>
                  </div>
                </div>
                <div className="control-group form-group">
                  <div className="controls">
                    <label>Confirm Password*</label>
                    <input
                      type="password"
                      className="form-control"
                      name="confirm_password"
                      id="confirm_password"
                      onChange={this.onChangeHandler}
                    ></input>
                  </div>
                </div>
                <div id="success"></div>

                <button
                  type="button"
                  onClick={() => {
                    this.registerButtonPressed();
                  }}
                  className="btn btn-primary"
                  id="login-btn"
                >
                  Register
                </button>
              </form>
            </div>
          </div>
        </div>
        <Footer/>
      </div>
    );
  }
}
export default Register;
