import React, { Component } from "react";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";

class Register extends Component {
  constructor() {
    super();

    this.state = {
      input: {},

      errors: {},
    };

    this.handleChange = this.handleChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    let input = this.state.input;

    input[event.target.name] = event.target.value;

    this.setState({
      input,
    });
  }
  handleSubmit(event) {
    event.preventDefault();

    if (this.validate()) {
      console.log(this.state);

      let input = {};

      input["password"] = "";

      input["confirm-password"] = "";

      this.setState({ input: input });

      alert("Register Form is submited");
    }
  }
  validate() {
    let input = this.state.input;

    let errors = {};

    let isValid = true;
    if (
      typeof input["password"] !== "undefined" &&
      typeof input["confirm-password"] !== "undefined"
    ) {
      if (input["password"] !== input["pass2"]) {
        isValid = false;

        errors["confirm-password"] = "Passwords don't match.";
      }
    }

    this.setState({
      errors: errors,
    });

    return isValid;
  }
  render() {
    return (
      <div className="container all-margin-top text-left minimum-height">
        <h1 className="mt-4 mb-3">
          Register
          <small>Form</small>
        </h1>
        <ol className="breadcrumb">
          <li className="breadcrumb-item active">Register</li>
        </ol>

        <div className="row">
          <div className="col-lg-4 mb-4">
            <form name="sentMessage" id="contactForm" noValidate>
              <div className="control-group form-group">
                <div className="controls">
                  <label>First Name*</label>
                  <input
                    type="text"
                    className="form-control"
                    id="first_name"
                    required
                    data-validation-required-message="Please enter your first name."
                  ></input>
                  <p className="help-block"></p>
                </div>
              </div>
              <div className="controls">
                <label>Last Name*</label>
                <input
                  type="text"
                  className="form-control"
                  id="last_name"
                  required
                  data-validation-required-message="Please enter your last name."
                ></input>
                <p className="help-block"></p>
              </div>
              <div className="control-group form-group">
                <div className="controls">
                  <label>Email*</label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    required
                    data-validation-required-message="Please enter your email."
                  ></input>
                  <p className="help-block"></p>
                </div>
              </div>
              <div className="control-group form-group">
                <div className="controls">
                  <label>Password*</label>
                  <input
                    type="password"
                    className="form-control"
                    name="password"
                    id="password"
                    required
                    data-validation-required-message="Please enter your password."
                    value={this.state.input.password}

                    onChange={this.handleChange}
                  ></input>
                </div>
              </div>
              <div className="control-group form-group">
                <div className="controls">
                  <label>Confirm Password*</label>
                  <input
                    type="password"
                    className="form-control"
                    name="confirm_password"
                    id="confirm_password"
                    required
                    data-validation-required-message="Please confirm your password."
                    value={this.state.input.confirm_password}

                    onChange={this.handleChange}
                  ></input>
                </div>
              </div>
              <div id="success"></div>

              <button type="submit" className="btn btn-primary" id="login-btn">
                Register
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default Register;
